from enum import StrEnum


class ApprovalStatus(StrEnum):
    """Possible states for a deferred tool approval."""

    PENDING = "pending"
    APPROVED = "approved"
    REJECTED = "rejected"


class ApprovalRecommendationVerdict(StrEnum):
    """Verdict options generated by the approval manager."""

    APPROVE = "approve"
    REJECT = "reject"
    HOLD = "hold"
